import React, { useState, useCallback, useMemo, useEffect } from 'react';
import AsyncSelect from 'react-select/async';
import { db } from '../firebase';
import { collection, getDocs, query, limit, where, orderBy, startAt, endAt } from 'firebase/firestore';
import './EmbroideryCalculator.css';

const MAIN_SIZES = ['S', 'M', 'L', 'XL', '2XL', '3XL'];
const LARGE_SIZES = ['2XL', '3XL', '4XL'];

const LoadingSpinner = () => (
  <div className="loading-spinner">
    <div className="spinner"></div>
  </div>
);

const getPriceForQuantity = (product, totalQuantity) => {
  if (totalQuantity >= 72) return parseFloat(product.Price_72_plus) || 0;
  if (totalQuantity >= 48) return parseFloat(product.Price_48_71) || 0;
  if (totalQuantity >= 24) return parseFloat(product.Price_24_47) || 0;
  if (totalQuantity >= 12) return parseFloat(product.Price_12_23) || 0;
  if (totalQuantity >= 6) return parseFloat(product.Price_6_11) || 0;
  return parseFloat(product.Price_2_5) || 0;
};

export default function EmbroideryCalculator() {
  const [productDatabase, setProductDatabase] = useState({});
  const [orders, setOrders] = useState([
    {
      STYLE_No: '',
      COLOR_NAME: '',
      quantities: {},
      error: null,
    },
  ]);
  const [errorMessage, setErrorMessage] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const updateOrder = useCallback(
    (index, field, value) => {
      setOrders((prevOrders) => {
        const newOrders = [...prevOrders];
        newOrders[index] = {
          ...newOrders[index],
          [field]: value,
          error: null,
        };

        if (field === 'STYLE_No') {
          newOrders[index].COLOR_NAME = '';
          newOrders[index].quantities = {};
        } else if (field === 'COLOR_NAME') {
          newOrders[index].quantities = {};
        }

        return newOrders;
      });
    },
    []
  );

  useEffect(() => {
    const fetchProductDataForOrders = async () => {
      const ordersToFetch = orders.filter((order) => {
        return (
          order.STYLE_No &&
          order.COLOR_NAME &&
          !productDatabase[`${order.STYLE_No}_${order.COLOR_NAME}`]
        );
      });
      for (const order of ordersToFetch) {
        const styleNo = order.STYLE_No;
        const colorName = order.COLOR_NAME;
        try {
          setIsLoading(true);
          const productQuery = query(
            collection(db, 'embroidery_products'),
            where('STYLE_No', '==', styleNo),
            where('COLOR_NAME', '==', colorName),
            limit(1)
          );
          const querySnapshot = await getDocs(productQuery);
          if (!querySnapshot.empty) {
            const productData = querySnapshot.docs[0].data();
            setProductDatabase((prevDatabase) => ({
              ...prevDatabase,
              [`${styleNo}_${colorName}`]: productData,
            }));
          } else {
            console.error('Product not found for style and color');
          }
        } catch (err) {
          console.error('Error fetching product data:', err);
          setErrorMessage('Failed to load product data. Please try again later.');
        } finally {
          setIsLoading(false);
        }
      }
    };
    fetchProductDataForOrders();
  }, [orders, productDatabase]);

  const calculateOrderTotals = useCallback(() => {
    const totalQuantity = orders.reduce((acc, order) => {
      return acc + Object.values(order.quantities).reduce((sum, qty) => sum + (qty || 0), 0);
    }, 0);

    const totals = orders.reduce(
      (acc, order) => {
        const product = productDatabase[`${order.STYLE_No}_${order.COLOR_NAME}`];
        if (!product) return acc;

        const basePrice = getPriceForQuantity(product, totalQuantity);

        const orderPrice = Object.entries(order.quantities).reduce((sum, [size, qty]) => {
          if (!qty) return sum;
          const surcharge = LARGE_SIZES.includes(size) ? parseFloat(product.Surcharge) || 0 : 0;
          return sum + (basePrice + surcharge) * qty;
        }, 0);

        return {
          quantity: acc.quantity + totalQuantity,
          price: acc.price + orderPrice,
        };
      },
      { quantity: 0, price: 0 }
    );

    return totals;
  }, [orders, productDatabase]);

  const totals = useMemo(() => calculateOrderTotals(), [calculateOrderTotals]);

  const loadStyleOptions = async (inputValue) => {
    try {
      const styleQuery = query(
        collection(db, 'embroidery_products'),
        orderBy('STYLE_No'),
        startAt(inputValue),
        endAt(inputValue + '\uf8ff'),
        limit(20)
      );
      const querySnapshot = await getDocs(styleQuery);
      const styles = [];
      querySnapshot.forEach((doc) => {
        const data = doc.data();
        if (data.STYLE_No && !styles.some(style => style.value === data.STYLE_No)) {
          styles.push({ label: data.STYLE_No, value: data.STYLE_No });
        }
      });
      return styles;
    } catch (err) {
      console.error('Error loading styles:', err);
      return [];
    }
  };

  const loadColorOptions = async (inputValue, styleNo) => {
    if (!styleNo) return [];
    try {
      const colorQuery = query(
        collection(db, 'embroidery_products'),
        where('STYLE_No', '==', styleNo),
        orderBy('COLOR_NAME'),
        startAt(inputValue),
        endAt(inputValue + '\uf8ff'),
        limit(20)
      );
      const querySnapshot = await getDocs(colorQuery);
      const colors = [];
      querySnapshot.forEach((doc) => {
        const data = doc.data();
        if (data.COLOR_NAME && !colors.some(color => color.value === data.COLOR_NAME)) {
          colors.push({ label: data.COLOR_NAME, value: data.COLOR_NAME });
        }
      });
      return colors;
    } catch (err) {
      console.error('Error loading colors:', err);
      return [];
    }
  };

  return (
    <div className="embroidery-calculator">
      <h1>Embroidery Order Form</h1>
      {errorMessage && (
        <div className="error-message" role="alert">
          <strong>Error: </strong>
          <span>{errorMessage}</span>
        </div>
      )}
      {isLoading && <LoadingSpinner />}
      <div className="table-container">
        <table>
          <thead>
            <tr>
              <th style={{ width: '200px' }}>Style No</th>
              <th style={{ width: '200px' }}>Color Name</th>
              <th style={{ width: '300px' }}>Product Title</th>
              {MAIN_SIZES.map((size) => (
                <th key={size}>{size}</th>
              ))}
              <th>Other Sizes</th>
              <th>Line Qty</th>
              <th>Row Total</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {orders.map((order, index) => {
              const product = productDatabase[`${order.STYLE_No}_${order.COLOR_NAME}`];
              const availableSizes = product
                ? Object.keys(product)
                    .filter((key) => key.includes('Size_') && !MAIN_SIZES.includes(key.replace('Size_', '')))
                    .map((key) => key.replace('Size_', ''))
                    .sort((a, b) => {
                      const sizeOrder = ['XS', 'S', 'M', 'L', 'XL', '2XL', '3XL', '4XL', '5XL', '6XL'];
                      return sizeOrder.indexOf(a) - sizeOrder.indexOf(b);
                    })
                : [];
              const lineQuantity = Object.values(order.quantities).reduce(
                (sum, qty) => sum + (qty || 0),
                0
              );

              return (
                <tr key={index}>
                  <td>
                    <AsyncSelect
                      cacheOptions
                      loadOptions={loadStyleOptions}
                      onChange={(selectedOption) => {
                        const value = selectedOption ? selectedOption.value : '';
                        updateOrder(index, 'STYLE_No', value);
                      }}
                      value={
                        order.STYLE_No
                          ? { value: order.STYLE_No, label: order.STYLE_No }
                          : null
                      }
                      placeholder="Enter or select style"
                      isDisabled={isLoading}
                      styles={{
                        control: (provided) => ({
                          ...provided,
                          width: '200px',
                        }),
                      }}
                    />
                  </td>
                  <td>
                    <AsyncSelect
                      cacheOptions
                      loadOptions={(inputValue) => loadColorOptions(inputValue, order.STYLE_No)}
                      onChange={(selectedOption) => {
                        const value = selectedOption ? selectedOption.value : '';
                        updateOrder(index, 'COLOR_NAME', value);
                      }}
                      value={
                        order.COLOR_NAME
                          ? { value: order.COLOR_NAME, label: order.COLOR_NAME }
                          : null
                      }
                      placeholder="Select Color"
                      isDisabled={!order.STYLE_No || isLoading}
                      styles={{
                        control: (provided) => ({
                          ...provided,
                          width: '200px',
                        }),
                      }}
                    />
                  </td>
                  <td>{product?.PRODUCT_TITLE || ''}</td>
                  {MAIN_SIZES.map((size) => (
                    <td
                      key={size}
                      className={LARGE_SIZES.includes(size) ? 'highlight-large-size' : ''}
                    >
                      <div>
                        <input
                          type="number"
                          value={order.quantities[size] || ''}
                          onChange={(e) =>
                            updateOrder(index, 'quantities', {
                              ...order.quantities,
                              [size]: parseInt(e.target.value) || 0,
                            })
                          }
                          min="0"
                          disabled={isLoading}
                        />
                        <div className="price-info">
                          {product
                            ? `$${(
                                getPriceForQuantity(product, totals.quantity) +
                                (LARGE_SIZES.includes(size) ? parseFloat(product.Surcharge) || 0 : 0)
                              ).toFixed(2)}`
                            : ''}
                        </div>
                      </div>
                    </td>
                  ))}
                  <td>
                    {availableSizes.map((size) => (
                      <div key={size} className="other-sizes-input">
                        <span>{size}: </span>
                        <input
                          type="number"
                          value={order.quantities[size] || ''}
                          onChange={(e) =>
                            updateOrder(index, 'quantities', {
                              ...order.quantities,
                              [size]: parseInt(e.target.value) || 0,
                            })
                          }
                          min="0"
                          disabled={isLoading}
                        />
                      </div>
                    ))}
                  </td>
                  <td>{lineQuantity}</td>
                  <td>
                    {product
                      ? `$${Object.entries(order.quantities)
                          .reduce((sum, [size, qty]) => {
                            const price = getPriceForQuantity(product, totals.quantity);
                            const surcharge = LARGE_SIZES.includes(size)
                              ? parseFloat(product.Surcharge) || 0
                              : 0;
                            return sum + (price + surcharge) * (qty || 0);
                          }, 0)
                          .toFixed(2)}`
                      : ''}
                  </td>
                  <td>
                    <button
                      onClick={() => {
                        setOrders((prevOrders) => prevOrders.filter((_, i) => i !== index));
                      }}
                    >
                      Remove
                    </button>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
      <button
        onClick={() => {
          setOrders((prevOrders) => [
            ...prevOrders,
            { STYLE_No: '', COLOR_NAME: '', quantities: {}, error: null },
          ]);
        }}
      >
        Add Order
      </button>
      <div className="order-summary">
        <h2>Order Summary</h2>
        <p>Total Quantity: {totals.quantity}</p>
        <p>Total Price: ${totals.price.toFixed(2)}</p>
      </div>
    </div>
  );
}
