import React, { useState, useCallback, useMemo, useEffect } from 'react';
import AsyncSelect from 'react-select/async';
import { db } from '../firebase';
import { collection, getDocs, query, where } from 'firebase/firestore';
import './EmbroideryCalculator.css';

const MAIN_SIZES = ['S', 'M', 'L', 'XL', '2XL', '3XL'];
const LARGE_SIZES = ['2XL', '3XL', '4XL'];

const LoadingSpinner = () => (
  <div className="loading-spinner">
    <div className="spinner"></div>
  </div>
);

const getPriceForQuantity = (product, totalQuantity) => {
  console.log('Calculating price for quantity:', totalQuantity);
  if (totalQuantity >= 72) return parseFloat(product.Price_72_plus) || 0;
  if (totalQuantity >= 48) return parseFloat(product.Price_48_71) || 0;
  if (totalQuantity >= 24) return parseFloat(product.Price_24_47) || 0;
  if (totalQuantity >= 12) return parseFloat(product.Price_12_23) || 0;
  if (totalQuantity >= 6) return parseFloat(product.Price_6_11) || 0;
  return parseFloat(product.Price_2_5) || 0;
};

export default function EmbroideryCalculator() {
  console.log('EmbroideryCalculator component rendered');
  const [productDatabase, setProductDatabase] = useState({});
  const [orders, setOrders] = useState([
    {
      STYLE_No: '',
      COLOR_NAME: '',
      quantities: {},
      error: null,
    },
  ]);
  const [styles, setStyles] = useState([]);
  const [colors, setColors] = useState({});
  const [errorMessage, setErrorMessage] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const fetchAllStyles = useCallback(async () => {
    console.log('Fetching all styles...');
    setIsLoading(true);
    setErrorMessage(null);
    let retries = 3;
    while (retries > 0) {
      try {
        console.log('Creating Firestore query...');
        const stylesQuery = query(collection(db, 'embroidery_products'));
        console.log('Executing query...');
        const querySnapshot = await getDocs(stylesQuery);
        console.log('Query executed. Number of documents:', querySnapshot.size);
        
        const stylesData = {};
        const styleSet = new Set();
        querySnapshot.forEach((doc) => {
          const data = doc.data();
          console.log('Document data:', data);
          if (data.STYLE_No) {
            styleSet.add(data.STYLE_No);
            if (!stylesData[data.STYLE_No]) {
              stylesData[data.STYLE_No] = data;
            }
          }
        });

        console.log('Unique styles found:', styleSet.size);
        setStyles(Array.from(styleSet));
        setProductDatabase(stylesData);
        console.log('Fetched styles:', stylesData);
        break; // Exit the loop if successful
      } catch (err) {
        console.error('Error fetching styles:', err);
        retries--;
        if (retries === 0) {
          setErrorMessage('Failed to load styles. Please try again later.');
        } else {
          console.log(`Retrying... ${retries} attempts left`);
          await new Promise(resolve => setTimeout(resolve, 2000)); // Wait 2 seconds before retrying
        }
      }
    }
    setIsLoading(false);
    console.log('Finished fetching styles.');
  }, []);

  useEffect(() => {
    console.log('useEffect triggered, calling fetchAllStyles');
    fetchAllStyles();
  }, [fetchAllStyles]);

  // ... rest of the component code ...

  return (
    <div className="embroidery-calculator">
      <h1>Embroidery Order Form</h1>
      {errorMessage && (
        <div className="error-message" role="alert">
          <strong>Error: </strong>
          <span>{errorMessage}</span>
        </div>
      )}
      {isLoading && <LoadingSpinner />}
      {/* ... rest of the JSX ... */}
    </div>
  );
}
