import React, { useState, useCallback, useMemo, useEffect } from 'react';
import AsyncSelect from 'react-select/async';
import { db } from '../firebase';
import { collection, getDocs, query, limit, where, orderBy, startAt, endAt, doc, getDoc } from 'firebase/firestore';

const MAIN_SIZES = ['XS', 'S', 'M', 'L', 'XL', '2XL', '3XL', '4XL', '5XL', '6XL'];

const LoadingSpinner = () => (
  <div className="flex justify-center items-center">
    <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-green-500"></div>
  </div>
);

const getPriceForQuantity = (basePrice, totalQuantity) => {
  if (totalQuantity >= 72) return parseFloat(basePrice['72_plus']) || 0;
  if (totalQuantity >= 48) return parseFloat(basePrice['48_71']) || 0;
  if (totalQuantity >= 24) return parseFloat(basePrice['24_47']) || 0;
  if (totalQuantity >= 12) return parseFloat(basePrice['12_23']) || 0;
  if (totalQuantity >= 6) return parseFloat(basePrice['6_11']) || 0;
  return parseFloat(basePrice['2_5']) || 0;
};

export default function EmbroideryCalculator() {
  const [productDatabase, setProductDatabase] = useState({});
  const [orders, setOrders] = useState([
    {
      styleNo: '',
      colorName: '',
      quantities: {},
      error: null,
    },
  ]);
  const [errorMessage, setErrorMessage] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const updateOrder = useCallback(
    (index, field, value) => {
      setOrders((prevOrders) => {
        const newOrders = [...prevOrders];
        newOrders[index] = {
          ...newOrders[index],
          [field]: value,
          error: null,
        };

        if (field === 'styleNo') {
          newOrders[index].colorName = '';
          newOrders[index].quantities = {};
        } else if (field === 'colorName') {
          newOrders[index].quantities = {};
        }

        return newOrders;
      });
    },
    []
  );

  useEffect(() => {
    const fetchProductDataForOrders = async () => {
      const ordersToFetch = orders.filter((order) => {
        return (
          order.styleNo &&
          order.colorName &&
          !productDatabase[`${order.styleNo}_${order.colorName}`]
        );
      });
      for (const order of ordersToFetch) {
        const styleNo = order.styleNo;
        const colorName = order.colorName;
        try {
          setIsLoading(true);
          const styleDoc = await getDoc(doc(db, 'styles', styleNo));
          if (styleDoc.exists()) {
            const styleData = styleDoc.data();
            const productQuery = query(
              collection(db, 'embroidery_products'),
              where('STYLE_No', '==', styleNo),
              where('COLOR_NAME', '==', colorName),
              limit(1)
            );
            const productSnapshot = await getDocs(productQuery);
            if (!productSnapshot.empty) {
              const productData = productSnapshot.docs[0].data();
              setProductDatabase((prevDatabase) => ({
                ...prevDatabase,
                [`${styleNo}_${colorName}`]: {
                  ...styleData,
                  ...productData,
                },
              }));
            } else {
              console.error('Product not found for style and color');
            }
          } else {
            console.error('Style not found');
          }
        } catch (err) {
          console.error('Error fetching product data:', err);
          setErrorMessage('Failed to load product data. Please try again later.');
        } finally {
          setIsLoading(false);
        }
      }
    };
    fetchProductDataForOrders();
  }, [orders, productDatabase]);

  const calculateOrderTotals = useCallback(() => {
    const totalQuantity = orders.reduce((acc, order) => {
      return acc + Object.values(order.quantities).reduce((sum, qty) => sum + (qty || 0), 0);
    }, 0);

    const totals = orders.reduce(
      (acc, order) => {
        const product = productDatabase[`${order.styleNo}_${order.colorName}`];
        if (!product) return acc;

        const basePrice = getPriceForQuantity(product.basePrice, totalQuantity);

        const orderPrice = Object.entries(order.quantities).reduce((sum, [size, qty]) => {
          if (!qty) return sum;
          const upcharge = product.sizeUpcharges ? product.sizeUpcharges[size] || 0 : 0;
          return sum + (basePrice + upcharge) * qty;
        }, 0);

        return {
          quantity: acc.quantity + Object.values(order.quantities).reduce((sum, qty) => sum + (qty || 0), 0),
          price: acc.price + orderPrice,
        };
      },
      { quantity: 0, price: 0 }
    );

    return totals;
  }, [orders, productDatabase]);

  const totals = useMemo(() => calculateOrderTotals(), [calculateOrderTotals]);

  const loadStyleOptions = async (inputValue) => {
    try {
      const lowercaseInput = inputValue.toLowerCase();
      const styleQuery = query(
        collection(db, 'styles'),
        orderBy('__name__'),
        startAt(lowercaseInput),
        endAt(lowercaseInput + '\uf8ff'),
        limit(20)
      );
      const querySnapshot = await getDocs(styleQuery);
      const styles = [];
      querySnapshot.forEach((doc) => {
        styles.push({ label: `${doc.id}`, value: doc.id });
      });
      return styles;
    } catch (err) {
      console.error('Error loading styles:', err);
      return [];
    }
  };

  const loadColorOptions = async (inputValue, styleNo) => {
    if (!styleNo) return [];
    try {
      const styleDoc = await getDoc(doc(db, 'styles', styleNo));
      if (styleDoc.exists()) {
        const styleData = styleDoc.data();
        return styleData.colors
          .filter(color => color.toLowerCase().includes(inputValue.toLowerCase()))
          .map(color => ({ label: color, value: color }));
      }
      return [];
    } catch (err) {
      console.error('Error loading colors:', err);
      return [];
    }
  };

  return (
    <div className="bg-gray-100 min-h-screen p-4">
      <div className="max-w-7xl mx-auto bg-white rounded-lg shadow-lg overflow-hidden">
        <h1 className="text-2xl font-bold text-green-600 p-4 bg-green-100">Embroidery Order Form</h1>
        {errorMessage && (
          <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-3 mb-4" role="alert">
            <p className="font-bold">Error</p>
            <p>{errorMessage}</p>
          </div>
        )}
        {isLoading && <LoadingSpinner />}
        <div className="overflow-x-auto">
          <table className="w-full text-sm">
            <thead className="bg-green-500 text-white">
              <tr>
                <th className="p-2 text-left">Style No</th>
                <th className="p-2 text-left">Color</th>
                <th className="p-2 text-left">Product Title</th>
                {MAIN_SIZES.map((size) => (
                  <th key={size} className="p-2 text-center">{size}</th>
                ))}
                <th className="p-2 text-center">Other</th>
                <th className="p-2 text-center">Qty</th>
                <th className="p-2 text-center">Total</th>
                <th className="p-2 text-center">Actions</th>
              </tr>
            </thead>
            <tbody>
              {orders.map((order, index) => {
                const product = productDatabase[`${order.styleNo}_${order.colorName}`];
                const availableSizes = product ? 
                  [...(product.sizes || []), ...(product.nonStandardSizes || [])].filter(size => !MAIN_SIZES.includes(size)) : 
                  [];
                const lineQuantity = Object.values(order.quantities).reduce(
                  (sum, qty) => sum + (qty || 0),
                  0
                );

                return (
                  <tr key={index} className={index % 2 === 0 ? 'bg-gray-50' : 'bg-white'}>
                    <td className="p-2">
                      <AsyncSelect
                        cacheOptions
                        loadOptions={loadStyleOptions}
                        onChange={(selectedOption) => {
                          const value = selectedOption ? selectedOption.value : '';
                          updateOrder(index, 'styleNo', value);
                        }}
                        value={
                          order.styleNo
                            ? { value: order.styleNo, label: order.styleNo }
                            : null
                        }
                        placeholder="Enter style number"
                        isDisabled={isLoading}
                        className="w-full text-sm"
                      />
                    </td>
                    <td className="p-2">
                      <AsyncSelect
                        cacheOptions
                        loadOptions={(inputValue) => loadColorOptions(inputValue, order.styleNo)}
                        onChange={(selectedOption) => {
                          const value = selectedOption ? selectedOption.value : '';
                          updateOrder(index, 'colorName', value);
                        }}
                        value={
                          order.colorName
                            ? { value: order.colorName, label: order.colorName }
                            : null
                        }
                        placeholder="Select Color"
                        isDisabled={!order.styleNo || isLoading}
                        className="w-full text-sm"
                      />
                    </td>
                    <td className="p-2 text-xs">{product?.brand || ''}</td>
                    {MAIN_SIZES.map((size) => (
                      <td
                        key={size}
                        className={`p-2 text-center`}
                      >
                        <input
                          type="number"
                          value={order.quantities[size] || ''}
                          onChange={(e) =>
                            updateOrder(index, 'quantities', {
                              ...order.quantities,
                              [size]: parseInt(e.target.value) || 0,
                            })
                          }
                          min="0"
                          disabled={isLoading || (product?.sizes && !product.sizes.includes(size))}
                          className="w-12 text-center border rounded p-1 text-sm"
                        />
                      </td>
                    ))}
                    <td className="p-2">
                      <select
                        onChange={(e) => {
                          const [size, qty] = e.target.value.split('|');
                          updateOrder(index, 'quantities', {
                            ...order.quantities,
                            [size]: parseInt(qty) || 0,
                          });
                        }}
                        className="w-full text-sm"
                      >
                        <option value="">Select size</option>
                        {availableSizes.map((size) => (
                          <option key={size} value={`${size}|${order.quantities[size] || 0}`}>
                            {size}: {order.quantities[size] || 0}
                          </option>
                        ))}
                      </select>
                    </td>
                    <td className="p-2 text-center font-bold">{lineQuantity}</td>
                    <td className="p-2 text-center font-bold">
                      {product
                        ? `$${Object.entries(order.quantities)
                            .reduce((sum, [size, qty]) => {
                              const price = getPriceForQuantity(product.basePrice, totals.quantity);
                              const upcharge = product.sizeUpcharges ? product.sizeUpcharges[size] || 0 : 0;
                              return sum + (price + upcharge) * (qty || 0);
                            }, 0)
                            .toFixed(2)}`
                        : ''}
                    </td>
                    <td className="p-2 text-center">
                      <button
                        onClick={() => {
                          setOrders((prevOrders) => prevOrders.filter((_, i) => i !== index));
                        }}
                        className="bg-red-500 text-white px-2 py-1 rounded text-xs hover:bg-red-600 transition-colors"
                      >
                        Remove
                      </button>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
        <div className="p-4 flex justify-between items-center bg-gray-50">
          <button
            onClick={() => {
              setOrders((prevOrders) => [
                ...prevOrders,
                { styleNo: '', colorName: '', quantities: {}, error: null },
              ]);
            }}
            className="bg-green-500 text-white px-4 py-2 rounded text-sm hover:bg-green-600 transition-colors"
          >
            Add Order
          </button>
          <div className="text-right">
            <p className="text-lg">Total Quantity: <span className="font-bold">{totals.quantity}</span></p>
            <p className="text-lg">Total Price: <span className="font-bold">${totals.price.toFixed(2)}</span></p>
          </div>
        </div>
      </div>
    </div>
  );
}
