import React, { useState, useEffect } from 'react';
import AsyncSelect from 'react-select/async';
import { db } from '../firebase';
import {
  collection,
  getDocs,
  query,
  limit,
  orderBy,
  startAt,
  endAt,
} from 'firebase/firestore';

export default function EmbroideryCalculator() {
  const [orders, setOrders] = useState([
    {
      styleNo: '',
      colorName: '',
      productTitle: '',
      productData: null,
      quantities: {},
      otherQuantity: 0,
    },
  ]);
  const [errorMessage, setErrorMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const MAIN_SIZES = ['XS', 'S', 'M', 'L', 'XL', '2XL', '3XL', '4XL', '5XL'];

  useEffect(() => {
    console.log('Orders updated:', orders);
  }, [orders]);

  useEffect(() => {
    // Check if Firebase is initialized correctly
    if (!db) {
      console.error('Firestore db is not initialized');
      setErrorMessage('Firebase initialization failed. Please check your configuration.');
    }
  }, []);

  const loadStyleOptions = async (inputValue) => {
    try {
      setIsLoading(true);
      const searchTerm = inputValue.toUpperCase();
      console.log('Searching for styles with input:', searchTerm);

      if (!db) {
        throw new Error('Firestore db is not initialized');
      }

      const stylesRef = collection(db, 'styles');

      const styleQuery = query(
        stylesRef,
        orderBy('styleNo'),
        startAt(searchTerm),
        endAt(searchTerm + '\uf8ff'),
        limit(20)
      );

      const querySnapshot = await getDocs(styleQuery);
      console.log('Query snapshot size:', querySnapshot.size);

      const styles = [];
      querySnapshot.forEach((doc) => {
        const data = doc.data();
        const styleNo = data.styleNo;
        console.log('Document ID:', doc.id, 'Data:', data);
        if (styleNo && data.productTitle) {
          styles.push({ label: `${styleNo} - ${data.productTitle}`, value: doc.id });
        }
      });

      console.log('Styles:', styles);
      return styles;
    } catch (err) {
      console.error('Error loading styles:', err);
      setErrorMessage(`Error loading styles: ${err.message}`);
      return [];
    } finally {
      setIsLoading(false);
    }
  };

  const updateOrder = (index, field, value) => {
    setOrders((prevOrders) => {
      const updatedOrders = [...prevOrders];
      updatedOrders[index] = {
        ...updatedOrders[index],
        [field]: value,
      };
      return updatedOrders;
    });
  };

  // ... rest of the component code remains the same

  return (
    <div className="bg-gray-100 min-h-screen p-4">
      <div className="max-w-7xl mx-auto bg-white rounded-lg shadow-lg overflow-hidden">
        <h1 className="text-2xl font-bold text-green-600 p-4 bg-green-100">
          Embroidery Order Form
        </h1>
        {errorMessage && (
          <div
            className="bg-red-100 border-l-4 border-red-500 text-red-700 p-3 mb-4"
            role="alert"
          >
            <p className="font-bold">Error</p>
            <p>{errorMessage}</p>
          </div>
        )}
        {isLoading && <div>Loading...</div>}
        <div className="overflow-x-auto">
          <table className="w-full text-sm">
            <thead className="bg-green-500 text-white">
              <tr>
                <th className="p-2 text-left" style={{ width: '200px' }}>
                  Style No
                </th>
                <th className="p-2 text-left">Color</th>
                <th className="p-2 text-left">Product Title</th>
                {MAIN_SIZES.map((size) => (
                  <th key={size} className="p-2 text-center">
                    {size}
                  </th>
                ))}
                <th className="p-2 text-center">Other</th>
                <th className="p-2 text-center">Qty</th>
                <th className="p-2 text-center">Total</th>
                <th className="p-2 text-center">Actions</th>
              </tr>
            </thead>
            <tbody>
              {orders.map((order, index) => (
                <tr
                  key={order.styleNo || index}
                  className={index % 2 === 0 ? 'bg-gray-50' : 'bg-white'}
                >
                  <td className="p-2" style={{ width: '200px' }}>
                    <AsyncSelect
                      cacheOptions
                      loadOptions={loadStyleOptions}
                      onChange={(selectedOption) => {
                        const docId = selectedOption ? selectedOption.value : '';
                        updateOrder(index, 'styleNo', docId);
                        updateOrder(index, 'colorName', '');
                        updateOrder(index, 'productTitle', '');
                        updateOrder(index, 'productData', null);
                      }}
                      value={
                        order.styleNo
                          ? {
                              value: order.styleNo,
                              label: `${order.styleNo} - ${order.productTitle}`,
                            }
                          : null
                      }
                      placeholder="Enter style number"
                      isDisabled={isLoading}
                      styles={{
                        control: (provided) => ({ ...provided, minWidth: '200px' }),
                        menu: (provided) => ({ ...provided, zIndex: 9999 }),
                      }}
                    />
                  </td>
                  {/* ... rest of the row content */}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        {/* ... rest of the component JSX */}
      </div>
    </div>
  );
}
