import React, { useState, useCallback, useMemo, useEffect } from 'react';
import AsyncSelect from 'react-select/async';
import { db } from '../firebase';
import { collection, getDocs, query, limit, startAfter, orderBy, where } from 'firebase/firestore';
import './EmbroideryCalculator.css';

const MAIN_SIZES = ['S', 'M', 'L', 'XL', '2XL', '3XL'];
const LARGE_SIZES = ['2XL', '3XL', '4XL'];

const LoadingSpinner = () => (
  <div className="loading-spinner">
    <div className="spinner"></div>
  </div>
);

const getPriceForQuantity = (product, totalQuantity) => {
  console.log('Calculating price for quantity:', totalQuantity);
  if (totalQuantity >= 72) return parseFloat(product.Price_72_plus) || 0;
  if (totalQuantity >= 48) return parseFloat(product.Price_48_71) || 0;
  if (totalQuantity >= 24) return parseFloat(product.Price_24_47) || 0;
  if (totalQuantity >= 12) return parseFloat(product.Price_12_23) || 0;
  if (totalQuantity >= 6) return parseFloat(product.Price_6_11) || 0;
  return parseFloat(product.Price_2_5) || 0;
};

export default function EmbroideryCalculator() {
  console.log('EmbroideryCalculator component rendered');
  const [productDatabase, setProductDatabase] = useState({});
  const [orders, setOrders] = useState([
    {
      STYLE_No: '',
      COLOR_NAME: '',
      quantities: {},
      error: null,
    },
  ]);
  const [styles, setStyles] = useState([]);
  const [colors, setColors] = useState({});
  const [errorMessage, setErrorMessage] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  // ... (keep all the existing functions: fetchAllStyles, fetchColors, updateOrder, calculateOrderTotals)

  const totals = useMemo(() => calculateOrderTotals(), [calculateOrderTotals]);

  return (
    <div className="embroidery-calculator">
      <h1>Embroidery Order Form</h1>
      {errorMessage && (
        <div className="error-message" role="alert">
          <strong>Error: </strong>
          <span>{errorMessage}</span>
        </div>
      )}
      {isLoading && <LoadingSpinner />}
      <div className="table-container">
        <table>
          <thead>
            <tr>
              <th style={{width: '200px'}}>Style No</th>
              <th style={{width: '200px'}}>Color Name</th>
              <th style={{width: '300px'}}>Product Title</th>
              {MAIN_SIZES.map((size) => (
                <th key={size}>{size}</th>
              ))}
              <th>Other Sizes</th>
              <th>Line Qty</th>
              <th>Row Total</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {orders.map((order, index) => {
              const product = productDatabase[order.STYLE_No]?.colors?.[order.COLOR_NAME];
              const availableSizes = product ? Object.keys(product).filter((size) => !MAIN_SIZES.includes(size)).sort((a, b) => {
                const sizeOrder = ['XS', 'S', 'M', 'L', 'XL', '2XL', '3XL', '4XL', '5XL', '6XL'];
                return sizeOrder.indexOf(a) - sizeOrder.indexOf(b);
              }) : [];
              const lineQuantity = Object.values(order.quantities).reduce((sum, qty) => sum + (qty || 0), 0);

              return (
                <tr key={index}>
                  <td>
                    <AsyncSelect
                      cacheOptions
                      loadOptions={async (inputValue) => {
                        console.log('Loading styles with input:', inputValue);
                        const filteredStyles = styles.filter((style) =>
                          style.toLowerCase().includes(inputValue.toLowerCase())
                        );
                        return filteredStyles.map((style) => ({ label: style, value: style }));
                      }}
                      defaultOptions={styles.map((style) => ({ label: style, value: style }))}
                      onChange={(selectedOption) => {
                        const value = selectedOption ? selectedOption.value : '';
                        updateOrder(index, 'STYLE_No', value);
                      }}
                      value={
                        order.STYLE_No
                          ? { value: order.STYLE_No, label: order.STYLE_No }
                          : null
                      }
                      placeholder="Enter or select style"
                      isDisabled={isLoading}
                      styles={{
                        control: (provided) => ({
                          ...provided,
                          width: '200px',
                        }),
                      }}
                    />
                  </td>
                  <td>
                    <AsyncSelect
                      cacheOptions
                      loadOptions={async (inputValue) => {
                        if (!order.STYLE_No) return [];
                        const filteredColors = colors[order.STYLE_No]?.filter((color) =>
                          color.toLowerCase().includes(inputValue.toLowerCase())
                        ) || [];
                        return filteredColors.map((color) => ({ label: color, value: color }));
                      }}
                      defaultOptions={colors[order.STYLE_No]?.map((color) => ({ label: color, value: color })) || []}
                      onChange={(selectedOption) => {
                        const value = selectedOption ? selectedOption.value : '';
                        updateOrder(index, 'COLOR_NAME', value);
                      }}
                      value={
                        order.COLOR_NAME
                          ? { value: order.COLOR_NAME, label: order.COLOR_NAME }
                          : null
                      }
                      placeholder="Select Color"
                      isDisabled={!order.STYLE_No || isLoading}
                      styles={{
                        control: (provided) => ({
                          ...provided,
                          width: '200px',
                        }),
                      }}
                    />
                  </td>
                  <td>{product?.PRODUCT_TITLE || ''}</td>
                  {MAIN_SIZES.map((size) => (
                    <td key={size} className={LARGE_SIZES.includes(size) ? 'highlight-large-size' : ''}>
                      <div>
                        <input
                          type="number"
                          value={order.quantities[size] || ''}
                          onChange={(e) =>
                            updateOrder(index, 'quantities', {
                              ...order.quantities,
                              [size]: parseInt(e.target.value) || 0,
                            })
                          }
                          min="0"
                          disabled={isLoading}
                        />
                        <div className="price-info">
                          {product ? `$${(getPriceForQuantity(product, totals.quantity) + (LARGE_SIZES.includes(size) ? parseFloat(product.Surcharge) || 0 : 0)).toFixed(2)}` : ''}
                        </div>
                      </div>
                    </td>
                  ))}
                  <td>
                    {availableSizes.map((size) => (
                      <div key={size}>
                        <span>{size}: </span>
                        <input
                          type="number"
                          value={order.quantities[size] || ''}
                          onChange={(e) =>
                            updateOrder(index, 'quantities', {
                              ...order.quantities,
                              [size]: parseInt(e.target.value) || 0,
                            })
                          }
                          min="0"
                          disabled={isLoading}
                        />
                      </div>
                    ))}
                  </td>
                  <td>{lineQuantity}</td>
                  <td>
                    {product
                      ? `$${(
                          Object.entries(order.quantities).reduce((sum, [size, qty]) => {
                            const price = getPriceForQuantity(product, totals.quantity);
                            const surcharge = LARGE_SIZES.includes(size) ? parseFloat(product.Surcharge) || 0 : 0;
                            return sum + (price + surcharge) * (qty || 0);
                          }, 0)
                        ).toFixed(2)}`
                      : ''}
                  </td>
                  <td>
                    <button onClick={() => {
                      setOrders((prevOrders) => prevOrders.filter((_, i) => i !== index));
                    }}>
                      Remove
                    </button>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
      <button
        onClick={() => {
          setOrders((prevOrders) => [
            ...prevOrders,
            { STYLE_No: '', COLOR_NAME: '', quantities: {}, error: null },
          ]);
        }}
      >
        Add Order
      </button>
      <div className="order-summary">
        <h2>Order Summary</h2>
        <p>Total Quantity: {totals.quantity}</p>
        <p>Total Price: ${totals.price.toFixed(2)}</p>
      </div>
    </div>
  );
}
