import React, { useState, useCallback, useMemo, useEffect } from 'react';
import axios from 'axios';
import AsyncSelect from 'react-select/async';
import debounce from 'lodash/debounce';
import './EmbroideryCalculator.css';

const API_BASE_URL = 'https://c3eku948.caspio.com/rest/v2/views/Heroku/records';
const MAIN_SIZES = ['S', 'M', 'L', 'XL', '2XL', '3XL'];

export default function EmbroideryCalculator() {
  const [productDatabase, setProductDatabase] = useState({});
  const [orders, setOrders] = useState([
    {
      STYLE_No: '',
      COLOR_NAME: '',
      quantities: {},
      error: null,
    },
  ]);
  const [colors, setColors] = useState({});
  const [errorMessage, setErrorMessage] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const abortController = React.useRef(null);

  useEffect(() => {
    console.log('Client ID:', process.env.REACT_APP_CASPIO_CLIENT_ID.substring(0, 5) + '...');
    console.log('Client Secret:', process.env.REACT_APP_CASPIO_CLIENT_SECRET.substring(0, 5) + '...');
  }, []);

  const refreshToken = useCallback(async () => {
    try {
      console.log('Refreshing token...');
      const response = await axios.post(
        'https://c3eku948.caspio.com/oauth/token',
        new URLSearchParams({
          grant_type: 'client_credentials',
          client_id: process.env.REACT_APP_CASPIO_CLIENT_ID,
          client_secret: process.env.REACT_APP_CASPIO_CLIENT_SECRET,
        }).toString(),
        {
          headers: {
            'Content-Type': 'application/x-www-form-urlencoded',
          },
        }
      );

      const newAccessToken = response.data.access_token;
      const expiresIn = response.data.expires_in || 3600;

      localStorage.setItem('caspioAccessToken', newAccessToken);
      console.log('Token refreshed successfully');

      return { newAccessToken, expiresIn };
    } catch (error) {
      console.error('Failed to refresh access token:', error);
      throw error;
    }
  }, []);

  const getAccessToken = useCallback(async () => {
    let accessToken = localStorage.getItem('caspioAccessToken');

    if (!accessToken) {
      console.log('No token found, refreshing...');
      const { newAccessToken } = await refreshToken();
      accessToken = newAccessToken;
    }

    return accessToken;
  }, [refreshToken]);

  const fetchColors = useCallback(async (styleNo) => {
    if (!styleNo) return;

    setIsLoading(true);
    try {
      console.log(`Fetching colors for style: ${styleNo}`);
      const accessToken = await getAccessToken();
      const query = `q={"STYLE_No":"${styleNo}"}&q.select=COLOR_NAME&q.distinct=true&q.sort=COLOR_NAME`;
      const response = await axios.get(`${API_BASE_URL}?${query}`, {
        headers: {
          Authorization: `Bearer ${accessToken}`,
        },
      });

      console.log('Color options response:', response.data);

      if (response.data?.Result?.length > 0) {
        const colorOptions = response.data.Result.map((item) => item.COLOR_NAME);
        setColors((prevColors) => ({
          ...prevColors,
          [styleNo]: colorOptions,
        }));
      }
    } catch (err) {
      console.error('Error fetching colors:', err);
      setErrorMessage('Failed to load color options. Please try again later.');
    } finally {
      setIsLoading(false);
    }
  }, [getAccessToken]);

  const loadStyles = useCallback(
    debounce(async (inputValue) => {
      if (!inputValue || inputValue.length < 2) {
        console.log('Input too short for style search.');
        return [];
      }

      console.log('Loading styles for input:', inputValue);
      if (abortController.current) {
        abortController.current.abort();
      }
      abortController.current = new AbortController();

      try {
        const accessToken = await getAccessToken();
        const query = `q={"STYLE_No":{"ilike":"%${inputValue}%"}}&q.select=STYLE_No&q.distinct=true&q.sort=STYLE_No&q.limit=100`;
        console.log('API request URL:', `${API_BASE_URL}?${query}`);
        const response = await axios.get(`${API_BASE_URL}?${query}`, {
          headers: {
            Authorization: `Bearer ${accessToken}`,
          },
          signal: abortController.current.signal,
        });

        console.log('API response:', response.data);

        if (response.data?.Result?.length > 0) {
          const styleOptions = response.data.Result.map((item) => ({
            value: item.STYLE_No,
            label: item.STYLE_No,
          }));

          console.log('Unique style options:', styleOptions);
          return styleOptions;
        } else {
          console.log('No styles found');
          return [];
        }
      } catch (err) {
        if (axios.isCancel(err)) {
          console.log('Request canceled', err.message);
        } else {
          console.error('Error loading styles:', err);
          setErrorMessage('Failed to load styles. Please try again.');
        }
        return [];
      }
    }, 300),
    [getAccessToken]
  );

  const updateOrder = useCallback(
    (index, field, value) => {
      console.log(`Updating order ${index}, field: ${field}, value:`, value);
      setOrders((prevOrders) => {
        const newOrders = [...prevOrders];
        newOrders[index] = {
          ...newOrders[index],
          [field]: value,
          error: null,
        };

        if (field === 'STYLE_No') {
          newOrders[index].COLOR_NAME = '';
          newOrders[index].quantities = {};
          if (value && !colors[value]) {
            fetchColors(value);
          }
        } else if (field === 'COLOR_NAME') {
          newOrders[index].quantities = {};
        }

        console.log('Updated orders:', newOrders);
        return newOrders;
      });
    },
    [colors, fetchColors]
  );

  const calculateLineQuantity = useCallback((order) => {
    return Object.values(order.quantities).reduce(
      (sum, qty) => sum + (parseInt(qty) || 0),
      0
    );
  }, []);

  const calculateRowTotal = useCallback((order) => {
    // This is a placeholder. You'll need to implement the actual pricing logic.
    return calculateLineQuantity(order) * 10; // Assuming $10 per item for now
  }, [calculateLineQuantity]);

  const totals = useMemo(() => {
    const totalQuantity = orders.reduce((acc, order) => acc + calculateLineQuantity(order), 0);
    const totalPrice = orders.reduce((acc, order) => acc + calculateRowTotal(order), 0);
    return { quantity: totalQuantity, price: totalPrice };
  }, [orders, calculateLineQuantity, calculateRowTotal]);

  return (
    <div className="embroidery-calculator">
      <h1>Embroidery Order Form</h1>
      {errorMessage && (
        <div className="error-message" role="alert">
          <strong>Error: </strong>
          <span>{errorMessage}</span>
        </div>
      )}
      {isLoading && <div className="loading-message">Loading...</div>}
      <table>
        <thead>
          <tr>
            <th>Style No</th>
            <th>Color Name</th>
            <th>Product Title</th>
            {MAIN_SIZES.map((size) => (
              <th key={size}>{size}</th>
            ))}
            <th>Other Sizes</th>
            <th>Line Qty</th>
            <th>Row Total</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {orders.map((order, index) => {
            const key = `${order.STYLE_No}-${order.COLOR_NAME}`;
            const product = productDatabase[key];
            const lineQuantity = calculateLineQuantity(order);
            const rowTotal = calculateRowTotal(order);

            return (
              <tr key={index}>
                <td>
                  <AsyncSelect
                    cacheOptions
                    loadOptions={loadStyles}
                    defaultOptions
                    onChange={(selectedOption) => {
                      const value = selectedOption ? selectedOption.value : '';
                      updateOrder(index, 'STYLE_No', value);
                    }}
                    value={
                      order.STYLE_No
                        ? { value: order.STYLE_No, label: order.STYLE_No }
                        : null
                    }
                    placeholder="Enter or select style"
                    isDisabled={isLoading}
                  />
                </td>
                <td>
                  <select
                    value={order.COLOR_NAME}
                    onChange={(e) => updateOrder(index, 'COLOR_NAME', e.target.value)}
                    disabled={!order.STYLE_No || isLoading}
                  >
                    <option value="">Select Color</option>
                    {colors[order.STYLE_No]?.map((color) => (
                      <option key={color} value={color}>
                        {color}
                      </option>
                    ))}
                  </select>
                </td>
                <td>{product?.PRODUCT_TITLE || ''}</td>
                {MAIN_SIZES.map((size) => (
                  <td key={size}>
                    <input
                      type="number"
                      value={order.quantities[size] || ''}
                      onChange={(e) =>
                        updateOrder(index, 'quantities', {
                          ...order.quantities,
                          [size]: e.target.value,
                        })
                      }
                      min="0"
                      disabled={isLoading}
                    />
                  </td>
                ))}
                <td>
                  {/* Add logic for "other sizes" as needed */}
                </td>
                <td>{lineQuantity}</td>
                <td>${rowTotal.toFixed(2)}</td>
                <td>
                  <button
                    onClick={() => setOrders(orders.filter((_, i) => i !== index))}
                    disabled={isLoading}
                  >
                    Remove
                  </button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
      <button
        onClick={() =>
          setOrders([...orders, { STYLE_No: '', COLOR_NAME: '', quantities: {}, error: null }])
        }
        disabled={isLoading}
      >
        Add Line
      </button>
      <button onClick={() => console.log('Submit order:', orders)} disabled={isLoading}>
        Submit Order
      </button>
      <div>
        <p>Total Quantity: {totals.quantity}</p>
        <p>Total Price: ${totals.price.toFixed(2)}</p>
      </div>
    </div>
  );
}
