import React, { useState, useEffect, useCallback } from 'react';
import AsyncSelect from 'react-select/async';
import { db } from '../firebase';
import { collection, getDocs, query, limit, where, orderBy } from 'firebase/firestore';

export default function EmbroideryCalculator() {
  const [orders, setOrders] = useState([
    {
      styleNo: '',
      colorName: '',
      productTitle: '',
      quantities: {},
      otherQuantity: 0,
      lineTotal: 0,
    },
  ]);
  const [errorMessage, setErrorMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [productDatabase, setProductDatabase] = useState({});

  const MAIN_SIZES = ['XS', 'S', 'M', 'L', 'XL', '2XL', '3XL', '4XL', '5XL'];

  // Load style options for the AsyncSelect component
  const loadStyleOptions = async (inputValue) => {
    try {
      const searchTerm = inputValue.toUpperCase();
      console.log('Searching for styles with input:', searchTerm);

      const stylesRef = collection(db, 'styles');
      console.log('Styles collection reference:', stylesRef);

      // Query based on document IDs (style_no)
      const styleQuery = query(
        stylesRef,
        orderBy('__name__'),
        where('__name__', '>=', searchTerm),
        where('__name__', '<=', searchTerm + '\uf8ff'),
        limit(20)
      );
      console.log('Style query:', styleQuery);

      const querySnapshot = await getDocs(styleQuery);
      console.log('Query snapshot size:', querySnapshot.size);

      const styles = [];
      const uniqueStyles = new Set();

      querySnapshot.forEach((doc) => {
        const data = doc.data();
        const styleNo = doc.id; // Using document ID as styleNo
        console.log('Document ID:', styleNo, 'Data:', data);
        if (styleNo && data.productTitle && !uniqueStyles.has(styleNo)) {
          styles.push({ label: `${styleNo} - ${data.productTitle}`, value: styleNo });
          uniqueStyles.add(styleNo);
        }
      });

      console.log('Styles found by styleNo (document ID):', styles);

      // If no results, try searching by productTitle
      if (styles.length === 0) {
        console.log('No styles found by styleNo, searching by productTitle');
        const titleQuery = query(
          stylesRef,
          orderBy('productTitle'),
          where('productTitle', '>=', searchTerm),
          where('productTitle', '<=', searchTerm + '\uf8ff'),
          limit(20)
        );
        console.log('Title query:', titleQuery);

        const titleSnapshot = await getDocs(titleQuery);
        console.log('Title query snapshot size:', titleSnapshot.size);

        titleSnapshot.forEach((doc) => {
          const data = doc.data();
          const styleNo = doc.id; // Using document ID as styleNo
          console.log('Document ID:', styleNo, 'Data:', data);
          if (styleNo && data.productTitle && !uniqueStyles.has(styleNo)) {
            styles.push({ label: `${styleNo} - ${data.productTitle}`, value: styleNo });
            uniqueStyles.add(styleNo);
          }
        });
      }

      console.log('Final returned styles:', styles);
      return styles;
    } catch (err) {
      console.error('Error loading styles:', err);
      setErrorMessage('Error loading styles. Please try again.');
      return [];
    }
  };

  // Function to update order fields
  const updateOrder = (index, field, value) => {
    const updatedOrders = [...orders];
    updatedOrders[index] = { ...updatedOrders[index], [field]: value };
    setOrders(updatedOrders);
  };

  // Function to fetch product data when styleNo is selected
  const fetchProductData = useCallback(async () => {
    setIsLoading(true);
    try {
      const productData = { ...productDatabase };

      for (const order of orders) {
        if (order.styleNo && !productData[order.styleNo]) {
          const docRef = collection(db, 'styles');
          const docSnap = await getDocs(query(docRef, where('__name__', '==', order.styleNo)));
          if (!docSnap.empty) {
            const doc = docSnap.docs[0];
            const data = doc.data();
            productData[order.styleNo] = data;
          }
        }
      }

      setProductDatabase(productData);
    } catch (error) {
      console.error('Error fetching product data:', error);
      setErrorMessage('Failed to fetch product data. Please try again.');
    } finally {
      setIsLoading(false);
    }
  }, [orders, productDatabase]);

  useEffect(() => {
    fetchProductData();
  }, [fetchProductData]);

  // Function to calculate line total
  const calculateLineTotal = (order) => {
    const product = productDatabase[order.styleNo];
    if (!product) return 0;

    const quantities = order.quantities;
    const totalQuantity = Object.values(quantities).reduce((sum, qty) => sum + (parseInt(qty) || 0), 0) + (parseInt(order.otherQuantity) || 0);

    // Determine base price based on totalQuantity
    const basePriceRanges = product.basePrice; // e.g., { '2_5': '39', '6_11': '32', ... }

    let basePrice = 0;
    const quantityRanges = Object.keys(basePriceRanges);
    for (const range of quantityRanges) {
      const [minStr, maxStr] = range.split('_');
      const min = parseInt(minStr);
      const max = maxStr === 'plus' ? Infinity : parseInt(maxStr);

      if (totalQuantity >= min && totalQuantity <= max) {
        basePrice = parseFloat(basePriceRanges[range]);
        break;
      }
    }

    if (basePrice === 0) {
      // If no base price found, return 0
      return 0;
    }

    // Now calculate total
    let lineTotal = 0;
    for (const size in quantities) {
      const qty = parseInt(quantities[size]) || 0;
      if (qty > 0) {
        let sizeUpcharge = 0;
        if (product.sizeUpcharges && product.sizeUpcharges[size]) {
          sizeUpcharge = parseFloat(product.sizeUpcharges[size]);
        }
        const price = basePrice + sizeUpcharge;
        lineTotal += price * qty;
      }
    }

    // Add other quantity
    lineTotal += basePrice * (parseInt(order.otherQuantity) || 0);

    return lineTotal;
  };

  return (
    <div className="bg-gray-100 min-h-screen p-4">
      <div className="max-w-7xl mx-auto bg-white rounded-lg shadow-lg overflow-hidden">
        <h1 className="text-2xl font-bold text-green-600 p-4 bg-green-100">Embroidery Order Form</h1>
        {errorMessage && (
          <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-3 mb-4" role="alert">
            <p className="font-bold">Error</p>
            <p>{errorMessage}</p>
          </div>
        )}
        {isLoading && <div className="p-4 text-center">Loading...</div>}
        <div className="overflow-x-auto">
          <table className="w-full text-sm">
            <thead className="bg-green-500 text-white">
              <tr>
                <th className="p-2 text-left" style={{ width: '200px' }}>Style No</th>
                <th className="p-2 text-left">Color</th>
                <th className="p-2 text-left">Product Title</th>
                {MAIN_SIZES.map((size) => (
                  <th key={size} className="p-2 text-center">{size}</th>
                ))}
                <th className="p-2 text-center">Other</th>
                <th className="p-2 text-center">Qty</th>
                <th className="p-2 text-center">Total</th>
                <th className="p-2 text-center">Actions</th>
              </tr>
            </thead>
            <tbody>
              {orders.map((order, index) => {
                const product = productDatabase[order.styleNo];
                const availableSizes = product ? product.sizes : [];

                const lineQuantity = Object.values(order.quantities).reduce(
                  (sum, qty) => sum + (parseInt(qty) || 0),
                  0
                ) + (parseInt(order.otherQuantity) || 0);

                const lineTotal = calculateLineTotal(order).toFixed(2);

                return (
                  <tr key={index} className={index % 2 === 0 ? 'bg-gray-50' : 'bg-white'}>
                    {/* Style No */}
                    <td className="p-2" style={{ width: '200px' }}>
                      <AsyncSelect
                        cacheOptions
                        loadOptions={loadStyleOptions}
                        onChange={(selectedOption) => {
                          const value = selectedOption ? selectedOption.value : '';
                          console.log('Selected style:', value);
                          updateOrder(index, 'styleNo', value);
                          updateOrder(index, 'colorName', '');
                          updateOrder(index, 'productTitle', '');

                          // Fetch product title when styleNo is selected
                          const fetchProductTitle = async () => {
                            try {
                              const docRef = collection(db, 'styles');
                              const docSnap = await getDocs(query(docRef, where('__name__', '==', value)));
                              if (!docSnap.empty) {
                                const doc = docSnap.docs[0];
                                const data = doc.data();
                                updateOrder(index, 'productTitle', data.productTitle || '');
                              }
                            } catch (error) {
                              console.error('Error fetching product title:', error);
                              setErrorMessage('Error fetching product title. Please try again.');
                            }
                          };
                          fetchProductTitle();
                        }}
                        value={
                          order.styleNo
                            ? { value: order.styleNo, label: order.styleNo }
                            : null
                        }
                        placeholder="Enter style number"
                        isDisabled={isLoading}
                        styles={{
                          control: (provided) => ({ ...provided, minWidth: '200px' }),
                          menu: (provided) => ({ ...provided, zIndex: 9999 }),
                        }}
                        onInputChange={(input) => console.log('Style input changed:', input)}
                      />
                    </td>
                    {/* Color selection */}
                    <td className="p-2">
                      {product && product.colors ? (
                        <select
                          className="w-full p-1 border rounded"
                          value={order.colorName || ''}
                          onChange={(e) => {
                            const value = e.target.value;
                            updateOrder(index, 'colorName', value);
                          }}
                        >
                          <option value="">Select Color</option>
                          {product.colors.map((color, idx) => (
                            <option key={idx} value={color}>
                              {color}
                            </option>
                          ))}
                        </select>
                      ) : (
                        <select className="w-full p-1 border rounded" disabled>
                          <option value="">Select Color</option>
                        </select>
                      )}
                    </td>
                    {/* Product Title */}
                    <td className="p-2">
                      {order.productTitle || ''}
                    </td>
                    {/* Sizes */}
                    {MAIN_SIZES.map((size) => (
                      <td key={size} className="p-2 text-center">
                        <input
                          type="number"
                          min="0"
                          className="w-16 p-1 border rounded"
                          value={order.quantities[size] || ''}
                          onChange={(e) => {
                            const value = e.target.value;
                            updateOrder(index, 'quantities', {
                              ...order.quantities,
                              [size]: value
                            });
                          }}
                          disabled={!product || !availableSizes.includes(size)}
                        />
                      </td>
                    ))}
                    {/* Other quantity */}
                    <td className="p-2 text-center">
                      <input
                        type="number"
                        min="0"
                        className="w-16 p-1 border rounded"
                        value={order.otherQuantity || ''}
                        onChange={(e) => {
                          const value = e.target.value;
                          updateOrder(index, 'otherQuantity', value);
                        }}
                      />
                    </td>
                    {/* Quantity total */}
                    <td className="p-2 text-center">
                      {lineQuantity}
                    </td>
                    {/* Line total */}
                    <td className="p-2 text-center">
                      ${lineTotal}
                    </td>
                    {/* Actions */}
                    <td className="p-2 text-center">
                      <button
                        className="bg-red-500 text-white px-2 py-1 rounded"
                        onClick={() => {
                          const updatedOrders = [...orders];
                          updatedOrders.splice(index, 1);
                          setOrders(updatedOrders);
                        }}
                      >
                        Remove
                      </button>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
        {/* Add Line Button */}
        <div className="p-4">
          <button
            className="bg-green-500 text-white px-4 py-2 rounded"
            onClick={() => {
              setOrders([...orders, {
                styleNo: '',
                colorName: '',
                productTitle: '',
                quantities: {},
                otherQuantity: 0,
                lineTotal: 0,
              }]);
            }}
          >
            Add Line
          </button>
        </div>
        {/* Order Total */}
        <div className="p-4 bg-gray-100">
          <h2 className="text-xl font-bold">Order Total: $
            {orders.reduce((total, order) => total + parseFloat(calculateLineTotal(order)), 0).toFixed(2)}
          </h2>
        </div>
      </div>
    </div>
  );
}
