// EmbroideryCalculator.js
import React, { useState, useEffect } from 'react';
import AsyncSelect from 'react-select/async';
import { db } from './firebase'; // Adjust the path if necessary
import {
  collection,
  doc,
  getDoc,
  getDocs,
  query,
  limit,
  orderBy,
  startAt,
  endAt,
} from 'firebase/firestore';

function EmbroideryCalculator() {
  const [orders, setOrders] = useState([
    {
      styleNo: '',
      colorName: '',
      productTitle: '',
      productData: null,
      quantities: {},
      otherQuantity: 0,
    },
  ]);
  const [errorMessage, setErrorMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const MAIN_SIZES = ['XS', 'S', 'M', 'L', 'XL', '2XL', '3XL', '4XL', '5XL'];

  // Function to load style options from Firestore
  const loadStyleOptions = async (inputValue) => {
    try {
      const searchTerm = inputValue.toUpperCase();

      const stylesRef = collection(db, 'styles');

      const styleQuery = query(
        stylesRef,
        orderBy('styleNo'),
        startAt(searchTerm),
        endAt(searchTerm + '\uf8ff'),
        limit(20)
      );

      const querySnapshot = await getDocs(styleQuery);

      const styles = [];
      querySnapshot.forEach((doc) => {
        const data = doc.data();
        const styleNo = data.styleNo;
        const productTitle = data.productTitle;
        if (styleNo && productTitle) {
          styles.push({ label: `${styleNo} - ${productTitle}`, value: doc.id });
        }
      });

      return styles;
    } catch (err) {
      console.error('Error loading styles:', err);
      setErrorMessage('Error loading styles. Please try again.');
      return [];
    }
  };

  // Function to update an order in the orders array
  const updateOrder = (index, field, value) => {
    setOrders((prevOrders) => {
      const updatedOrders = [...prevOrders];
      updatedOrders[index] = {
        ...updatedOrders[index],
        [field]: value,
      };
      return updatedOrders;
    });
  };

  // Function to calculate the line total for an order
  const calculateLineTotal = (order) => {
    const product = order.productData;
    if (!product) return 0;

    const quantities = order.quantities;
    const totalQuantity =
      Object.values(quantities).reduce((sum, qty) => sum + (parseInt(qty) || 0), 0) +
      (parseInt(order.otherQuantity) || 0);

    const basePriceRanges = product.basePrice;

    let basePrice = 0;
    const quantityRanges = Object.keys(basePriceRanges);
    for (const range of quantityRanges) {
      const [minStr, maxStr] = range.split('_');
      const min = parseInt(minStr);
      const max = maxStr === 'plus' ? Infinity : parseInt(maxStr);

      if (totalQuantity >= min && totalQuantity <= max) {
        basePrice = parseFloat(basePriceRanges[range]);
        break;
      }
    }

    if (basePrice === 0) {
      return 0;
    }

    let lineTotal = 0;
    for (const size in quantities) {
      const qty = parseInt(quantities[size]) || 0;
      if (qty > 0) {
        let sizeUpcharge = 0;
        if (product.sizeUpcharges && product.sizeUpcharges[size]) {
          sizeUpcharge = parseFloat(product.sizeUpcharges[size]);
        }
        const price = basePrice + sizeUpcharge;
        lineTotal += price * qty;
      }
    }

    // Handle 'Other' quantity
    const otherQty = parseInt(order.otherQuantity) || 0;
    if (otherQty > 0) {
      lineTotal += basePrice * otherQty;
    }

    return lineTotal;
  };

  return (
    <div className="bg-gray-100 min-h-screen p-4">
      <div className="max-w-7xl mx-auto bg-white rounded-lg shadow-lg overflow-hidden">
        <h1 className="text-2xl font-bold text-green-600 p-4 bg-green-100">
          Embroidery Order Form
        </h1>
        {errorMessage && (
          <div
            className="bg-red-100 border-l-4 border-red-500 text-red-700 p-3 mb-4"
            role="alert"
          >
            <p className="font-bold">Error</p>
            <p>{errorMessage}</p>
          </div>
        )}
        <div className="overflow-x-auto">
          <table className="w-full text-sm">
            <thead className="bg-green-500 text-white">
              <tr>
                <th className="p-2 text-left" style={{ width: '200px' }}>
                  Style No
                </th>
                <th className="p-2 text-left">Color</th>
                <th className="p-2 text-left">Product Title</th>
                {MAIN_SIZES.map((size) => (
                  <th key={size} className="p-2 text-center">
                    {size}
                  </th>
                ))}
                <th className="p-2 text-center">Other</th>
                <th className="p-2 text-center">Qty</th>
                <th className="p-2 text-center">Total</th>
                <th className="p-2 text-center">Actions</th>
              </tr>
            </thead>
            <tbody>
              {orders.map((order, index) => {
                const product = order.productData;

                const lineQuantity =
                  Object.values(order.quantities).reduce(
                    (sum, qty) => sum + (parseInt(qty) || 0),
                    0
                  ) + (parseInt(order.otherQuantity) || 0);

                const lineTotal = calculateLineTotal(order).toFixed(2);

                return (
                  <tr
                    key={order.styleNo || index}
                    className={index % 2 === 0 ? 'bg-gray-50' : 'bg-white'}
                  >
                    <td className="p-2" style={{ width: '200px' }}>
                      <AsyncSelect
                        cacheOptions
                        loadOptions={loadStyleOptions}
                        onChange={(selectedOption) => {
                          const docId = selectedOption ? selectedOption.value : '';
                          updateOrder(index, 'styleNo', docId);
                          updateOrder(index, 'colorName', '');
                          updateOrder(index, 'productTitle', '');
                          updateOrder(index, 'productData', null);

                          // Fetch product data
                          const fetchProductData = async () => {
                            try {
                              const docRef = doc(db, 'styles', docId);
                              const docSnap = await getDoc(docRef);
                              if (docSnap.exists()) {
                                const data = docSnap.data();
                                updateOrder(index, 'productTitle', data.productTitle || '');
                                updateOrder(index, 'productData', data);
                              } else {
                                setErrorMessage('No product data found for the selected style.');
                              }
                            } catch (error) {
                              console.error('Error fetching product data:', error);
                              setErrorMessage('Error fetching product data. Please try again.');
                            }
                          };
                          if (docId) {
                            fetchProductData();
                          }
                        }}
                        value={
                          order.styleNo
                            ? {
                                value: order.styleNo,
                                label: `${order.styleNo} - ${order.productTitle}`,
                              }
                            : null
                        }
                        placeholder="Enter style number"
                        styles={{
                          control: (provided) => ({ ...provided, minWidth: '200px' }),
                          menu: (provided) => ({ ...provided, zIndex: 9999 }),
                        }}
                      />
                    </td>
                    <td className="p-2">
                      {product && Array.isArray(product.colors) && product.colors.length > 0 ? (
                        <select
                          className="w-full p-1 border rounded"
                          value={order.colorName || ''}
                          onChange={(e) => {
                            const value = e.target.value;
                            updateOrder(index, 'colorName', value);
                          }}
                        >
                          <option value="">Select Color</option>
                          {product.colors.map((color, idx) => (
                            <option key={idx} value={color}>
                              {color}
                            </option>
                          ))}
                        </select>
                      ) : (
                        <select className="w-full p-1 border rounded" disabled>
                          <option value="">
                            {product ? 'No Colors Available' : 'Select Style First'}
                          </option>
                        </select>
                      )}
                    </td>
                    <td className="p-2">{order.productTitle || ''}</td>
                    {MAIN_SIZES.map((size) => (
                      <td key={size} className="p-2 text-center">
                        <input
                          type="number"
                          min="0"
                          className="w-16 p-1 border rounded"
                          value={order.quantities[size] || ''}
                          onChange={(e) => {
                            const value = e.target.value;
                            setOrders((prevOrders) => {
                              const updatedOrders = [...prevOrders];
                              const updatedQuantities = {
                                ...updatedOrders[index].quantities,
                                [size]: value,
                              };
                              updatedOrders[index] = {
                                ...updatedOrders[index],
                                quantities: updatedQuantities,
                              };
                              return updatedOrders;
                            });
                          }}
                          disabled={!product}
                        />
                      </td>
                    ))}
                    <td className="p-2 text-center">
                      <input
                        type="number"
                        min="0"
                        className="w-16 p-1 border rounded"
                        value={order.otherQuantity || ''}
                        onChange={(e) => {
                          const value = e.target.value;
                          updateOrder(index, 'otherQuantity', value);
                        }}
                        disabled={!product}
                      />
                    </td>
                    <td className="p-2 text-center">{lineQuantity}</td>
                    <td className="p-2 text-center">${lineTotal}</td>
                    <td className="p-2 text-center">
                      <button
                        className="bg-red-500 text-white px-2 py-1 rounded"
                        onClick={() => {
                          setOrders((prevOrders) => prevOrders.filter((_, i) => i !== index));
                        }}
                      >
                        Remove
                      </button>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
        <div className="p-4">
          <button
            className="bg-green-500 text-white px-4 py-2 rounded"
            onClick={() => {
              setOrders([
                ...orders,
                {
                  styleNo: '',
                  colorName: '',
                  productTitle: '',
                  productData: null,
                  quantities: {},
                  otherQuantity: 0,
                },
              ]);
            }}
          >
            Add Line
          </button>
        </div>
        <div className="p-4 bg-gray-100">
          <h2 className="text-xl font-bold">
            Order Total: $
            {orders
              .reduce((total, order) => total + parseFloat(calculateLineTotal(order)), 0)
              .toFixed(2)}
          </h2>
        </div>
      </div>
    </div>
  );
}

export default EmbroideryCalculator;
