import React, { useState, useCallback, useEffect } from 'react';
import { db } from '../firebase';
import { collection, getDocs, query } from 'firebase/firestore';
import './EmbroideryCalculator.css';

const MAIN_SIZES = ['S', 'M', 'L', 'XL', '2XL', '3XL'];
const LARGE_SIZES = ['2XL', '3XL', '4XL'];

const LoadingSpinner = () => (
  <div className="loading-spinner">
    <div className="spinner"></div>
  </div>
);

export default function EmbroideryCalculator() {
  const [productDatabase, setProductDatabase] = useState({});
  const [orders, setOrders] = useState([
    {
      STYLE_No: '',
      COLOR_NAME: '',
      quantities: {},
      error: null,
    },
  ]);
  const [styles, setStyles] = useState([]);
  const [colors, setColors] = useState({});
  const [errorMessage, setErrorMessage] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const fetchAllStyles = useCallback(async () => {
    console.log('Fetching all styles...');
    setIsLoading(true);
    try {
      const stylesQuery = query(collection(db, 'embroidery_orders'));
      console.log('Executing query...');
      const querySnapshot = await getDocs(stylesQuery);
      console.log('Query executed. Number of documents:', querySnapshot.size);
      
      const stylesData = {};
      const styleSet = new Set();
      querySnapshot.forEach((doc) => {
        const data = doc.data();
        console.log('Document data:', data);
        if (data.STYLE_No) {
          styleSet.add(data.STYLE_No);
          if (!stylesData[data.STYLE_No]) {
            stylesData[data.STYLE_No] = data;
          }
        }
      });

      console.log('Unique styles found:', styleSet.size);
      setStyles(Array.from(styleSet));
      setProductDatabase(stylesData);
      console.log('Fetched styles:', stylesData);
    } catch (err) {
      console.error('Error fetching styles:', err);
      setErrorMessage('Failed to load styles. Please try again.');
    } finally {
      setIsLoading(false);
      console.log('Finished fetching styles.');
    }
  }, []);

  useEffect(() => {
    fetchAllStyles();
  }, [fetchAllStyles]);

  return (
    <div className="embroidery-calculator">
      <h1>Embroidery Order Form</h1>
      {errorMessage && (
        <div className="error-message" role="alert">
          <strong>Error: </strong>
          <span>{errorMessage}</span>
        </div>
      )}
      {isLoading && <LoadingSpinner />}
      <div>
        <h2>Available Styles</h2>
        <ul>
          {styles.map((style) => (
            <li key={style}>{style}</li>
          ))}
        </ul>
      </div>
      <div>
        <h2>Product Database</h2>
        <pre>{JSON.stringify(productDatabase, null, 2)}</pre>
      </div>
      <div>
        <h2>Orders</h2>
        <pre>{JSON.stringify(orders, null, 2)}</pre>
      </div>
      <div>
        <h2>Colors</h2>
        <pre>{JSON.stringify(colors, null, 2)}</pre>
      </div>
    </div>
  );
}
