import React, { useState, useEffect, useRef } from 'react';
import { db } from '../firebase';
import {
  collection,
  getDocs,
  query,
  limit,
  orderBy,
  startAt,
  endAt,
  doc,
  getDoc,
} from 'firebase/firestore';
import debounce from 'lodash.debounce';

export default function EmbroideryCalculator() {
  const [order, setOrder] = useState({
    styleNo: '',
    colorName: '',
    productTitle: '',
    productData: null,
    quantities: {},
  });
  const [errorMessage, setErrorMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [styles, setStyles] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [showDropdown, setShowDropdown] = useState(false);
  const inputRef = useRef(null);

  useEffect(() => {
    if (!db) {
      console.error('Firestore db is not initialized');
      setErrorMessage('Firebase initialization failed. Please check your configuration.');
    }
  }, []);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (inputRef.current && !inputRef.current.contains(event.target)) {
        setShowDropdown(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const loadStyleOptions = async (inputValue) => {
    try {
      setIsLoading(true);
      const searchTerm = inputValue.toUpperCase();
      console.log('Searching for styles with input:', searchTerm);

      if (!db) {
        throw new Error('Firestore db is not initialized');
      }

      const stylesRef = collection(db, 'styles');

      const styleQuery = query(
        stylesRef,
        orderBy('styleNo'),
        startAt(searchTerm),
        endAt(searchTerm + '\uf8ff'),
        limit(20)
      );

      const querySnapshot = await getDocs(styleQuery);
      console.log('Query snapshot size:', querySnapshot.size);

      const loadedStyles = [];
      querySnapshot.forEach((doc) => {
        const data = doc.data();
        const styleNo = data.styleNo;
        if (styleNo) {
          loadedStyles.push({ label: styleNo, value: doc.id, data: data });
        }
      });

      console.log('Styles:', loadedStyles);
      setStyles(loadedStyles);
      setShowDropdown(true);
    } catch (err) {
      console.error('Error loading styles:', err);
      setErrorMessage(`Error loading styles: ${err.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  const debouncedLoadStyles = useRef(
    debounce((value) => loadStyleOptions(value), 300)
  ).current;

  const handleStyleSelect = async (style) => {
    try {
      setIsLoading(true);
      const styleDoc = await getDoc(doc(db, 'styles', style.value));
      if (styleDoc.exists()) {
        const styleData = styleDoc.data();
        setOrder((prevOrder) => ({
          ...prevOrder,
          styleNo: styleData.styleNo,
          productTitle: styleData.productTitle,
          productData: styleData,
          colorName: '', // Reset color selection
          quantities: {}, // Reset quantities
        }));
        setSearchTerm(styleData.styleNo);
        setShowDropdown(false);
      } else {
        setErrorMessage('Selected style not found');
      }
    } catch (err) {
      console.error('Error fetching style details:', err);
      setErrorMessage(`Error fetching style details: ${err.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  const handleColorSelect = (color) => {
    setOrder({
      ...order,
      colorName: color,
    });
  };

  const handleQuantityChange = (size, quantity) => {
    const qty = parseInt(quantity, 10);
    if (isNaN(qty) || qty < 0) return; // Ensure quantity is a non-negative integer
    setOrder((prevOrder) => ({
      ...prevOrder,
      quantities: {
        ...prevOrder.quantities,
        [size]: qty,
      },
    }));
  };

  const handleSubmit = () => {
    console.log('Order submitted:', order);
    // Implement your submission logic here
  };

  return (
    <div className="bg-gray-100 min-h-screen p-4">
      <div className="max-w-7xl mx-auto bg-white rounded-lg shadow-lg overflow-hidden">
        <h1 className="text-2xl font-bold text-green-600 p-4 bg-green-100">
          Embroidery Order Form
        </h1>
        {errorMessage && (
          <div
            className="bg-red-100 border-l-4 border-red-500 text-red-700 p-3 mb-4"
            role="alert"
          >
            <p className="font-bold">Error</p>
            <p>{errorMessage}</p>
          </div>
        )}
        {isLoading && (
          <div className="flex justify-center items-center">
            <div className="loader"></div>
          </div>
        )}
        <div className="p-4">
          <div className="flex space-x-4">
            <div className="flex-1 relative" ref={inputRef}>
              <label className="block text-sm font-medium text-gray-700">Style</label>
              <input
                type="text"
                value={searchTerm}
                placeholder="Search styles..."
                onChange={(e) => {
                  setSearchTerm(e.target.value);
                  debouncedLoadStyles(e.target.value);
                }}
                onFocus={() => {
                  if (styles.length > 0) setShowDropdown(true);
                }}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"
                aria-label="Search for styles"
              />
              {showDropdown && styles.length > 0 && (
                <ul className="absolute z-10 mt-1 w-full bg-white shadow-lg max-h-60 rounded-md py-1 text-base ring-1 ring-black ring-opacity-5 overflow-auto focus:outline-none sm:text-sm">
                  {styles.map((style) => (
                    <li
                      key={style.value}
                      className="cursor-pointer select-none relative py-2 pl-3 pr-9 hover:bg-gray-100"
                      onClick={() => handleStyleSelect(style)}
                    >
                      {style.label}
                    </li>
                  ))}
                </ul>
              )}
            </div>
            <div className="flex-1">
              <label className="block text-sm font-medium text-gray-700">Color</label>
              <select
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"
                onChange={(e) => handleColorSelect(e.target.value)}
                value={order.colorName}
                disabled={!order.productData}
                aria-label="Select color"
              >
                <option value="">Select Color</option>
                {order.productData && order.productData.colors && order.productData.colors.map((color, index) => (
                  <option key={index} value={color}>
                    {color}
                  </option>
                ))}
              </select>
            </div>
            <div className="flex-1">
              <label className="block text-sm font-medium text-gray-700">Description</label>
              <p className="mt-1">{order.productTitle}</p>
            </div>
          </div>
          {order.productData && (
            <div className="mt-4">
              <h3 className="text-lg font-medium text-gray-700">Sizing Matrix</h3>
              <div className="mt-2 flex flex-wrap">
                {order.productData.sizes && order.productData.sizes.map((size) => (
                  <div key={size} className="mr-4 mb-2">
                    <label className="block text-sm font-medium text-gray-700">{size}</label>
                    <input
                      type="number"
                      min="0"
                      value={order.quantities[size] || ''}
                      onChange={(e) => handleQuantityChange(size, e.target.value)}
                      className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"
                      aria-label={`Quantity for size ${size}`}
                    />
                  </div>
                ))}
              </div>
            </div>
          )}
          <div className="mt-4">
            <button
              type="button"
              className="px-4 py-2 bg-green-600 text-white rounded-md hover:bg-green-700 focus:outline-none disabled:opacity-50"
              disabled={!order.styleNo || !order.colorName}
              onClick={handleSubmit}
            >
              Submit Order
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
